	private static int waysToSplit(int[] nums) {

		int ways = 0;

		int mod = 1000000007;

		int[] presum = new int[nums.length + 1];

		for(int i = 0 ; i < nums.length ; i++) {
			presum[i + 1] += presum[i] + nums[i];
		}

		for(int i = 1 ; i + 2 < presum.length ; i++) {
			
			int l = search(presum, presum[i],i, true);
			if(l == -1) continue;
			
			int r = search(presum, presum[i],i, false);
			if(r == -1) continue;
			
			ways = (ways + (r - l + 1) % mod) % mod;
		}

		return ways;
	}

	private static int search(int[] A, int leftSum, int index, boolean b) {

		int N = A.length;
		int l = index, r = N - 2;
		int res = -1;

		while (l <= r) {

			int m = (r - l) / 2 + l;
			int midSum = A[m] - A[index];
			int rightSum = A[N - 1] - A[m];

			if (leftSum <= midSum && midSum <= rightSum) {
				res = m;
				if (b) r = m - 1;
				else l = m + 1;
			} else if (leftSum > midSum) {  // shrink left
				l = m + 1;
			} else {  // shrink right
				r = m - 1;
			}

		}
		return res;
	}
	
